import React, { useState } from 'react'
import JQuery from 'jquery'

function Register (): JSX.Element {
  const [login, setLogin] = useState('')
  const [password, setPassword] = useState('')
  const [repeatedPassword, setRepeatedPassword] = useState('')
  const [email, setEmail] = useState('')

  const onLoginChange = (event: any): void => {
    setLogin(event.target.value)
  }

  const onEmailChange = (event: any): void => {
    setEmail(event.target.value)
  }

  const onPassChange = (event: any): void => {
    setPassword(event.target.value)
  }

  const onRepeatedPassChange = (event: any): void => {
    setRepeatedPassword(event.target.value)
  }

  const submitRegister = (): void => {
    JQuery.ajax({
      url: 'http://localhost:8000/api/auth/signup',
      data: {
        username: login,
        email,
        password,
        repeatedPassword
      },
      type: 'POST'
    }).done(function (result: any) {
      console.log(result)
    })
  }

  return <form className={'card col-lg-3 m-auto'}>
    <h3 className={'card-header'}>Register Form</h3>
    <div className={'card-body'}>
      <label className={'form-label'}>
          <input type={'text'} className={'form-control'} placeholder={'Login'} value={login} onChange={onLoginChange}/>
          <input type={'email'} className={'form-control mt-2'} placeholder={'Email'} value={email} onChange={onEmailChange}/>
          <input type={'password'} className={'form-control mt-2'} placeholder={'Password'} value={password} onChange={onPassChange}/>
          <input type={'password'} className={'form-control mt-2'} placeholder={'Repeat password'} value={repeatedPassword} onChange={onRepeatedPassChange}/>
          <input type={'submit'} value={'Sign up'} className={'btn btn-primary mt-2'} onClick={submitRegister}/>
      </label>
    </div>
  </form>
}

export default Register
