import React from 'react'
import './css/task.css'
import $ from 'jquery'
import Cookies from "js-cookie";
import {TOKEN_COOKIE_NAME} from "../index";
function Task (props: any): JSX.Element {
  const sendDeleteRequest = () => {
    $.ajax({
      url: 'http://localhost:8000/api/boards/' + props.boardId + '/columns/' + props.element.statusId + '/tasks/' + props.element.id,
      type: 'DELETE',
      beforeSend: function (xhr) {
        /* eslint-disable-next-line @typescript-eslint/restrict-plus-operands */
        xhr.setRequestHeader('Authorization', 'Bearer ' + Cookies.get(TOKEN_COOKIE_NAME))
      }
    })
  }

  return <div className={'task'}>
    <h2>{props.element.name}</h2>
    <p>{props.element.description}</p>
    <button className={'btn btn-danger'}>Delete</button>
  </div>
}

export default Task
