import * as React from 'react'
import './css/App.css'
import 'bootstrap/dist/css/bootstrap.min.css'
import { Link, Outlet, useLocation } from 'react-router-dom'
import LocalizedStrings, { LocalizedStringsMethods } from 'react-localization'
import { useState } from 'react'

export interface IStrings extends LocalizedStringsMethods{
  score: string
  time: String
}

const ENGLISH_LANG = 'en'; const RUSSIAN_LANG = 'ru'

const strings = new LocalizedStrings({
  ru: {
    signin: 'Войти',
    time: 'Tempo'
  },
  en: {
    signin: 'Sign In',
    time: 'Time'
  }
})

function App (): JSX.Element {
  const [lang, setLang] = useState(RUSSIAN_LANG)

  const isActive = (link: string): string => {
    return useLocation().pathname === link ? 'active' : ''
  }

  const changeLanguage = (): void => {
    const newLang = lang === ENGLISH_LANG ? RUSSIAN_LANG : ENGLISH_LANG
    strings.setLanguage(newLang)
    setLang(newLang)
  }

  return (
    <main className={'app-main'}>
      <header>
          <div className={'logo'}>
            <Link to={'/'}>Trello Copy</Link>
          </div>
          <div className={'links'}>
            <Link to={'login'} className={isActive('/login')}>{strings.signin}</Link>
            <Link to={'register'} className={isActive('/register')}>Sign up</Link>
            <Link to={'login'}>Edit profile</Link>
            <Link to={'login'}>Sign out</Link>
            <Link to={'login'}>Create new board</Link>
            <a href={'#'} onClick={changeLanguage}>en/ru</a>
          </div>
      </header>
      <section className={'main-content'}>
        <Outlet />
      </section>
        <footer>
          <h5 className={'text-white'}>{(new Date()).getFullYear()}</h5>
      </footer>
    </main>
  )
}

export default App
